// reducers/index.js
import { fetchPokemonType } from '../actions/pokeList_actions';

const initialState = {
    pokemon: [],
    error: null
}

export default (state = initialState, action) => {
    switch(action.type){
        case fetchPokemonType:
            return {
                ...state,
                pokemon: action.payload
            }
        default:
            return state;
    }
}
