// MADE AFTER REDUCER

import axios from 'axios';

// ACTION
export const fetchPokemonType = 'FETCH_POKEMON'
export const fetchPokemonFulfilledType = 'FETCH_POKEMON_FULLFILLED'
export const fetchPokemonRejectedType = 'FETCH_POKEMON_REJECTED';

// ACTION CREATORS
export const fetchPokemon = () => 
console.log('fetching')
        dispatch => ({ type: fetchPokemonType })
        axios.get('https://pokeapi.co/api/v2/pokemon-species/')
        .then((res => res.json())
        .then( pokemon => dispatch({ 
            type: fetchPokemonFulfilledType, payload: pokemon
        })
        )
        )


export const fetchPokemonFulfilled = () => {
    return function (dispatch) {
        dispatch({type: fetchPokemonFulfilledType })
    }
}
